<?php/** *	The template for displaying posts in the cwp_gallery post format. * *	@package WordPress *	@subpackage MaterialX */?><article id="post-<?php the_ID(); ?>" <?php post_class(); ?>>	<?php 		$metro_creativex_posttitle = get_the_title();		$metro_creativex_feat_image = wp_get_attachment_image_src( get_post_thumbnail_id( $post->ID ), 'single-post-thumbnail' );		if(isset($metro_creativex_feat_image[0])):			echo '<div class="img">					<div class="post_icon" style="background-image:url('.get_template_directory_uri().'/images/pt_gallery.png);"></div>					<img src='.$metro_creativex_feat_image[0].' alt="'.$metro_creativex_posttitle.'">				</div>';		endif;		?>	<div class="post_content">		<a href="<?php the_permalink(); ?>">			<?php 				the_title();								if(!isset($metro_creativex_feat_image[0])):					echo '<div class="post_icon" style="background-image:url('.get_template_directory_uri().'/images/pt_gallery.png);"></div>';				endif;				?>		</a>		<div class="post_date"><?php the_time( get_option( 'date_format' ) ); ?></div>	</div><!--/post_content--></article>