<?php/** *	The template for displaying posts in the Quote post format. * *	@package WordPress *	@subpackage MaterialX */?><article id="post-<?php the_ID(); ?>" <?php post_class(); ?>>	<div class="post_content">		<a href="<?php the_permalink(); ?>">			<?php 				the_title();				echo '<div class="post_icon" style="background-image:url('.get_template_directory_uri().'/images/pt_quote.png);"></div>';			?>		</a>		<div class="excerpt format" style="text-align: center;">			<?php  echo get_the_excerpt(); ?>		</div><!--/excerpt-->		<div class="post_date"><?php the_time( get_option( 'date_format' ) ); ?></div>	</div><!--/post_content--></article>